mapper.select:
select count(*)
  from t_wish
 WHERE id = #{id}
   AND book_code = #{bookCode}

mapper.insert:
    insert into t_Wish (       
	    book_code, id
	) values (
	    #{bookCode}, #{id}
	)

delete 
  from t_Wish 			
 where book_code = #{bookCode}
   and id = #{id}

mapper.select:
select review_no as reviewNo, book_code as bookCode, id, content, DATE_FORMAT(reg_date, '%Y-%c-%e %T') as regDate
  from t_review				
 where book_code = #{bookCode}

select review_no as reviewNo, book_code as bookCode, id, content, DATE_FORMAT(reg_date, '%Y-%c-%e %T') as regDate
  from t_review			
 where review_no = #{reviewNo}

mapper.insert:

	SELECT LAST_INSERT_ID()

    insert into t_review (    				
	    book_code, id, content   
	) values (
	    #{bookCode}, #{id}, #{content}
	)

mapper.update:
        update t_review  				
		   set content = #{content}
		 where review_no = #{reviewNo}

